import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import AddProduct from "./components/AddProduct";
import "./styles/main.css";
import CategoryPage from "./components/CategoryPage";

interface Product {
  id: number;
  category: string;
  brand: string;
  price: number;
}

const categories = ["Protein", "Creatine", "Amino Acids", "Vitamins"];

const Home = () => {
  return (
    <div className="container">
      <h1>Welcome to the Sports Nutrition Store</h1>
      <div className="category-list">
        {categories.map((category) => (
          <Link key={category} to={`/category/${category.toLowerCase()}`} className="category-card">
            {category}
          </Link>
        ))}
      </div>
    </div>
  );
};

const CategoryProducts = ({ category }: { category: string }) => {
  const [products, setProducts] = useState<Product[]>([]);

  useEffect(() => {
    fetch(`http://127.0.0.1:5000/products/category/${category}`)
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error("Error fetching products:", error));
  }, [category]);

  return (
    <div className="container">
      <h2>{category} Products</h2>
      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <h3>{product.brand}</h3>
              <p>Price: ${product.price}</p>
            </div>
          ))
        ) : (
          <p>No products available in this category.</p>
        )}
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <nav className="navbar">
        <Link to="/" className="nav-link">Home</Link>
        <Link to="/add-product" className="nav-link">Add Product</Link>
      </nav>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/add-product" element={<AddProduct />} />
        <Route path="/category/:category" element={<CategoryPage />} />
      </Routes>
    </Router>
  );
};

const CategoryProductsWrapper = () => {
  const pathParts = window.location.pathname.split("/");
  const category = pathParts[pathParts.length - 1];
  return <CategoryProducts category={category} />;
};

export default App;
